body {
  margin: 0;
  padding: 0;
  font-family: "Montserrat", sans-serif;
  font-weight: lighter;
  overflow: hidden;
  background-color: #eaeaea;
}

header {
  width: 100%;
  display: flex;
}

main {
  position: relative;
  width: 100%;
}

h1, h2, h3, h4, h5 {
  font-weight: bold;
}

img {
  width: 100%;
}

#alert-overlay, #drill-down-overlay, #initial-loader-overlay {
  display: none;
}
#alert-overlay article, #drill-down-overlay article, #initial-loader-overlay article {
  padding: 0;
  transition: all 0.2s ease-in-out;
}

#alert-overlay.active {
  position: absolute;
  z-index: 5;
  height: 100vh;
  width: 100vw;
  padding: 1em;
  background-color: rgba(255, 255, 255, 0.5);
  display: flex;
}
#alert-overlay.active article {
  margin: auto;
  position: relative;
  width: 30em;
  max-width: 100%;
  background-color: #444;
  padding: 4em 2em;
  transition: all 0.2s ease-in-out;
}
#alert-overlay.active article div {
  padding: 0.5em;
  background-color: #FF625B;
  color: #fff;
}
#alert-overlay.active article .close {
  position: absolute;
  top: 0.5em;
  right: 0.5em;
  font-weight: bold;
  color: #fff;
  border: 2px solid #fff;
  padding: 0.25em 0.5em;
  border-radius: 50%;
  cursor: pointer;
}

@media all and (min-width: 401px) {
  button {
    border: none;
    background-color: #9fd5b3;
    padding: 1em;
    font-weight: bold;
    color: #004544;
    cursor: pointer;
    transition: all 0.2s ease-in-out;
  }

  .filter button:hover {
    padding: 1.15em;
    box-shadow: -2px 2px 10px 0px rgba(159, 213, 179, 0.5);
    transition: all 0.2s ease-in-out;
  }
}
