	font-family: FontAwesome;
	text-rendering: auto;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
  }

//==============font awesome icons Ends================================

@import "radio.scss";
@import "loader.scss";
$base-duration: 500ms;
$cubic: cubic-bezier(.64,.09,.08,1);
// Colors
$color-1: #004544;
$color-2: #9fd5b3; 
$color-3: #E48F22;//library
$color-4: #CF3234; //Historic dist
$color-5: #444;
$color-6: #00AEDB; //Schools
$color-7: #00B159; //Park
$white: #fff;
// $color-1: #004544;
// $color-2: #23a696;
// $color-3: #9fd5b3;
// $color-4: #fff;
// $color-5: #444;
// $color-6: #FF625B;

// Breakpoints
$sm: new-breakpoint(min-width 320px);
$med: new-breakpoint(min-width 768px);
$lg: new-breakpoint(min-width 1024px);

::-webkit-input-placeholder { /* Chrome/Opera/Safari */
  color: $color-1;
}
::-moz-placeholder { /* Firefox 19+ */
  color: $color-1;
}
:-ms-input-placeholder { /* IE 10+ */
  color: $color-1;
}
:-moz-placeholder { /* Firefox 18- */
  color: $color-1;
}

*, *:before, *:after {
	box-sizing: border-box;
	outline: none;
}
body { margin:0; padding:0; font-family: 'Montserrat', sans-serif; font-weight: lighter; overflow: hidden; background-color: lighten( $color-5, 65% )}
header{width: 100%; display: flex;}
main {position: relative; width: 100%;}
h1,h2,h3,h4,h5{font-weight: bold}
img{width: 100%;}
// ====================== mobile ============================
#alert-overlay, #drill-down-overlay, #initial-loader-overlay{
	display: none;

	article{
		padding: 0;
		transition: all .2s ease-in-out;
	}
}

#alert-overlay.active{
	position: absolute;
	z-index: 5;
	height: 100vh;
	width: 100vw;
	padding: 1em;
	background-color: rgba($white, .5);
	display: flex;

	article{
		margin: auto;
		position: relative;
		width: 30em;
		max-width: 100%;
		background-color: $color-5;
		padding: 4em 2em;
		transition: all .2s ease-in-out;

		div{
			padding: .5em;
			background-color: $color-6;
			color: $white;
		}

		.close{
			position: absolute;
			top: .5em;
			right: .5em;
			font-weight: bold;
			color: $white;
			border: 2px solid $white;
			padding: .25em .5em;
			border-radius: 50%;
			cursor: pointer;
		}
	}
}


